package edu.pitt.backtracking;

/**
 * ********* Wildcard Matching #44 --- Accepted 460ms
 * Implement wildcard pattern matching with support for '?' and '*'.
 * '?' Matches any single character.
 * '*' Matches any sequence of characters (including the empty sequence).
 * The matching should cover the entire input string (not partial).
 * The function prototype should be:
 * bool isMatch(const char *s, const char *p)
 * Some examples:
 * isMatch("aa","a") → false
 * isMatch("aa","aa") → true
 * isMatch("aaa","aa") → false
 * isMatch("aa", "*") → true
 * isMatch("aa", "a*") → true
 * isMatch("ab", "?*") → true
 * isMatch("aab", "c*a*b") → false
 * @author yanma
 * @since 2015-06-11
 * @version 2015-06-11
 */
public class WildcardMatching_44 {
	
	public static boolean isMatch(String s, String p) {
        if (s == null || p == null) {
        	return false;
        } 
        
        int count = 0;
        for (char c : p.toCharArray()) {
			if (c != '*') count++;
		}
        if (count > s.length()) return false;
        
        if (s.length() == 0) {
        	return (p.length() == 0 || p.equals("*"));
        }
        
        int star = -1; //last star position
        int ss = -1; // last matched position
        
        int i = 0; //pointer in s
        int j = 0; // pointer in p
        
        while (i < s.length()) {
//			if (j == p.length() && i < s.length()) return false;
			if (j < p.length() && s.charAt(i) == p.charAt(j)) { //match
				i++; j++;
			} else if (j < p.length() && p.charAt(j) == '?') { // match
				i++; j++;
			} else if (j < p.length() && p.charAt(j) == '*') {
				star = j;
				ss = i - 1;
				j++;
			} else {
				if (star == -1) {
					return false;
				} else {
					j = star + 1;
					i = ss + 1;
					ss++;
				}
			}
		}
        
        if (j == p.length()) {
        	if (star == j - 1) {
        		return true;
        	}
        }
        
        if (i == s.length()) {
        	while (j < p.length()) {
				if (p.charAt(j) != '*') return false;
				j++;
			}
        }
        
        return true;
    }
    
    public static boolean helper(String s, String p, int i, int j) {
    	if (i == s.length()) {
    		if (j == p.length() - 1) {
    			return p.charAt(j) == '*';
    		} else if (j == p.length()) {
    			return true;
    		} else {
    			return false;
    		}
    	}
    	if (j == p.length() && i < s.length()) {
    		return false;
    	}
    	
    	
		if (p.charAt(j) != '*' && p.charAt(j) != '?') {
    		if (s.charAt(i) != p.charAt(j)) {
    			return false;
    		} else {
				return helper(s, p, i + 1, j + 1);
			}
    	} else if (p.charAt(j) == '?') {
			return helper(s, p, i + 1, j + 1);
		} else if (p.charAt(j) == '*') {
			while (j < p.length() && p.charAt(j) == '*') {
				j++;
			}
			while (i <= s.length()) {
				if (helper(s, p, i, j)) {
					return true;
				} 
				i++;
			}
		}
    	return false;
    }
	
	

    public static boolean isMatch1(String s, String p) {
        if (s == null || p == null) {
        	return false;
        } 
        int count = 0;
        for (char c : p.toCharArray()) {
			if (c != '*') count++;
		}
        if (count > s.length()) return false;
        
        if (s.length() == 0) {
        	return (p.length() == 0 || p.equals("*"));
        }
        
        boolean[][] truth = new boolean[s.length() + 1][p.length() + 1];
        truth[s.length()][p.length()] = true;
        isPossible(s, p, truth);
        return truth[0][0];
    }
    
    
    public static void isPossible(String s, String p, boolean[][] truth) {
    	for (int first = s.length() - 1; first >= 0; first--) {
			for (int second = p.length() - 1; second >= 0; second--) {
				if (p.charAt(second) != '*' && p.charAt(second) != '?') {					
					if ((s.charAt(first) == p.charAt(second)) && truth[first + 1][second + 1]) {
						truth[first][second] = true;
					}
				} else if (p.charAt(second) == '?') {
					if (truth[first + 1][second + 1]) {
						truth[first][second] = true;
					}
				} else if (p.charAt(second) == '*') {
					if (truth[first][second + 1] || (first < s.length() && truth[first + 1][second])) {
						truth[first][second] = true;
					}
				}
			}
		}
    }
	
    
	
	
	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(isMatch("aa","a"));
		System.out.println(isMatch("aa","aa"));
		System.out.println(isMatch("aaa","aa"));
		System.out.println(isMatch("aa", "*"));
		System.out.println(isMatch("aa", "a*"));
		System.out.println(isMatch("ab", "?*"));
		System.out.println(isMatch("aab", "c*a*b"));
		System.out.println(isMatch("aaabbbbbbbb", "a*?bbbb"));
//		System.out.println(isMatch("aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa", "*aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa*"));
//		String s = "";
//		String p = "**";
		String s = "abbabaaabbabbaababbabbbbbabbbabbbabaaaaababababbbabababaabbababaabbbbbbaaaabababbbaabbbbaabbbbababababbaabbaababaabbbababababbbbaaabbbbbabaaaabbababbbbaababaabbababbbbbababbbabaaaaaaaabbbbbaabaaababaaaabb";
		String p = "**aa*****ba*a*bb**aa*ab****a*aaaaaa***a*aaaa**bbabb*b*b**aaaaaaaaa*a********ba*bbb***a*ba*bb*bb**a*b*bb";
		System.out.println(isMatch(s, p));
	}

}
