package edu.pitt.string;

/**
 * ******* Shortest Palindrome #214 --- Accepted 372ms
 * Given a string S, you are allowed to convert it to a palindrome by adding characters in front of it. Find and return the shortest palindrome you can find by performing this transformation.
 * For example:
 * Given "aacecaaa", return "aaacecaaa".
 * Given "abcd", return "dcbabcd".
 * @author yanma
 * @since 2015-06-25
 * @version 2015-06-25
 */
public class ShortestPalindrome_214 {
	
	
	/**
	 * KMP prefix algorithm
	 * Accepted 372ms
	 * @param s
	 * @return
	 */
	public static String shortestPalindrome(String s) {  
        String result = "";  
        if(s.length() == 0)  
            return result;  
        int[] prefix = new int[s.length() * 2];  
        String mirror = s + new StringBuilder(s).reverse().toString();  
        for(int i = 1; i < s.length() * 2; i++) {  
            int j = prefix[i-1];  
            while(mirror.charAt(j) != mirror.charAt(i) && j > 0)  
                j = prefix[j-1];  
            if(mirror.charAt(i) == mirror.charAt(j))  
                prefix[i] = j + 1;  
            else  
                prefix[i] = 0;  
        }  
        int count = s.length() - prefix[s.length() * 2 -1];  
        result = new StringBuilder(s.substring(s.length()-count, s.length())).reverse().toString() + s;  
        return result;  
    }  
	
	
	/**
	 * TLE
	 * @param s
	 * @return
	 */
    public static String shortestPalindrome1(String s) {
        if (s == null || s.length() <= 1) {
        	return s;
        }
        
        int start = 0;
		int end = s.length() - 1;
		while (end > start) {
			if (!isPalindrome(s, start, end)) {
				end--;
				continue;
			}
			break;
		}
    	//s.subString(start, end + 1) is palindrome
		StringBuffer bf = new StringBuffer();
		for (int i = s.length() - 1; i >= end + 1; i--) {
			bf.append(s.charAt(i));
		}
				
    	return bf.append(s).toString();
    }
    
    public static boolean isPalindrome(String s, int start, int end) {
    	if (start > end) {
    		return false;
    	}
    	
    	while (start + 1 < end) {
    		if (s.charAt(start) != s.charAt(end)) {
    			return false;
    		}
    		start++;
    		end--;
    	}
    	
    	if (start + 1 == end) {
    		return s.charAt(start) == s.charAt(end);
    	}
    	
    	return true;
    }
    
	
	

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String s1 = "";
		String s2 = "aacecaaa";
		
		System.out.println(shortestPalindrome(s1));
		System.out.println(shortestPalindrome(s2));
		
	}

}
